mapper.update:
		UPDATE BOARD
		SET INQ_CNT = INQ_CNT + 1
        where BBS_NO = #{bbsNo}

		UPDATE BOARD
		SET BBS_TTL = #{bbsTtl},
			BBS_CN = #{bbsCn}
        where BBS_NO = #{bbsNo}

		UPDATE BOARD
		SET ATCH_YN = #{status}
        where BBS_NO = #{bbsNo}
        	and 
        	ATCH_YN != #{status}
