mapper.select:
SELECT count(SR_NO)
FROM SR_INFORMATION
WHERE SR_NO LIKE #{srCode}

SELECT COUNT(SI.SR_NO) as totalRow FROM SR_INFORMATION SI, SR_DEMAND SD
, TASK T
, SYSTEMS S1
, STATUS S2
, SR_RESOURCES R
WHERE SD.DMND_NO = SI.DMND_NO
AND SD.DMND_NO LIKE '%'||#{srInfoFilter.dmndNo}||'%'
AND SD.TTL LIKE '%'||#{srInfoFilter.ttl}||'%'
AND SD.TASK_SE_CD = T.TASK_SE_CD AND SD.TASK_SE_CD = #{srInfoFilter.taskSeCd}
AND S1.SYS_CD = SD.SYS_CD AND SD.SYS_CD = #{srInfoFilter.sysCd}
AND S2.STTS_CD = SD.STTS_CD AND SD.STTS_CD = #{srInfoFilter.sttsCd}
AND R.SR_NO = SI.SR_NO AND R.EMP_ID = #{srInfoFilter.empId}
AND SI.PIC_ID = #{srInfoFilter.empId}

SELECT SI.DMND_NO AS dmndNo, SI.SR_NO AS srNo, S1.SYS_NM AS sysNm, T.TASK_SE_NM AS taskSeNm, SD.TTL AS ttl, M.FLNM AS flnm, SI.BGNG_YMD AS bgngYmd, SI.END_YMD AS endYmd, S2.STTS_NM AS sttsNm FROM SR_INFORMATION SI, SR_DEMAND SD, TASK T, MEMBERS M, SYSTEMS S1, STATUS S2
, SR_RESOURCES R
WHERE SD.DMND_NO = SI.DMND_NO AND SD.TASK_SE_CD = T.TASK_SE_CD AND SD.CUST_ID = M.MEMBER_ID AND S1.SYS_CD = SD.SYS_CD AND S2.STTS_CD = SD.STTS_CD
AND SD.SYS_CD = #{srInfoFilter.sysCd}
AND SD.TASK_SE_CD = #{srInfoFilter.taskSeCd}
AND SD.STTS_CD = #{srInfoFilter.sttsCd}
AND SD.TTL LIKE '%'||#{srInfoFilter.ttl}||'%'
AND SD.DMND_NO LIKE '%'||#{srInfoFilter.dmndNo}||'%'
AND R.SR_NO = SI.SR_NO AND R.EMP_ID = #{srInfoFilter.empId}
AND SI.PIC_ID = #{srInfoFilter.empId}
order by dmndNo DESC
order by dmndNo ASC

   		SELECT  rn,  dmndNo, srNo
		 from(
		    select rownum as rn, dmndNo, srNo
		    from(
		        select 
		          SI.DMND_NO AS dmndNo,
		          SI.SR_NO AS srNo

		      FROM SR_INFORMATION SI, SR_DEMAND SD 
		      where si.dmnd_no = sd.dmnd_no
		      order by SD.DMND_YMD DESC
		         )        

		      ) a  , sr_information_history his
	      WHERE his.HSTRY_ID = #{hstryId}
	      and  his.sr_no = a.srNo
